config.CUSTOM:
      var flag=false;
      if (condition==null || condition=="") flag=true;
      var result={};
      result.table="CUSTOM";
      var columns="NAME,CUSTOM_NO,ADDR,TAX_NO,CITY,COUNTRY,CNAME,CADDR,CUSTOM_TYPE,STATION";
      var telCondition="";
      if (orderColumn!="TEL")
      {
      columns+=",(select TEL from AEMPK where CUSTOM_NO=CUSTOM.CUSTOM_NO and TEL is not null and ROWNUM<=1) as TEL";
	condition+=getFieldSQLCondition(orderColumn,fromValue,toValue,flag);
} else {
	telCondition=getFieldSQLCondition(orderColumn,fromValue,toValue,false);
	columns+=",(select TEL from AEMPK where CUSTOM_NO=CUSTOM.CUSTOM_NO "+telCondition+" and ROWNUM<=1) as TEL";
	if (flag==false) condition+=" and ";
	condition+=" (select count(*) from AEMPK where CUSTOM_NO=CUSTOM.CUSTOM_NO "+telCondition+")>0 ";
}
result.columns=columns;
result.condition=condition;
return result;
